function Parallax(){const a=document.getElementsByClassName("parallax"),t=window.screen.height;var e,l;window.addEventListener("scroll",function(){let o=this.pageYOffset;Math.abs(o);if(Math.abs(o)<=t){for(let t=0;t<a.length;t++)e=a[t],l=e.getAttribute("data-speed"),e.setAttribute("style","transform: translate(0px, "+o*l/100+"px");nav.style.top="-40px;"}else nav.className="0px"})}function noParallax(){document.getElementById("noparallax").setAttribute("display","none"),document.getElementById("parallax").setAttribute("display","none")}function castSmoothScroll(){$.srSmoothscroll({step:80,speed:300,ease:"linear"})}function toParallax_or_notToParallax(){const a=navigator.platform.toLowerCase();-1!=a.indexOf("ipad")||-1!=a.indexOf("iphone")?noParallax().srSmoothscroll():-1!=a.indexOf("win32")||-1!=a.indexOf("linux")?(Parallax(),$.browser.webkit&&castSmoothScroll()):Parallax()}document.window.onload=toParallax_or_notToParallax();