function Parallax(){const t=document.getElementsByClassName("parallax"),e=document.getElementById("TheStars"),a=(document.getElementById("title"),document.getElementById("title")),l=document.getElementById("TheSky"),n=document.getElementById("Nav"),s=window.screen.height;var d,o;window.addEventListener("scroll",function(){let i=this.pageYOffset,r=Math.abs(i)/s;if(Math.abs(i)<=s){for(let e=0;e<t.length;e++)d=t[e],o=d.getAttribute("data-speed"),d.setAttribute("style","transform: translate(0px, "+i*o/100+"px");l.style.opacity=1-r,Math.abs(i)/s>.1&&e.setAttribute("style","opacity: 100;")}else n.setAttribute("style","display: inline-block;"),a.setAttribute("style","display: none;")})}document.body.onload=Parallax();